let fs = require('fs')
let modules = fs.readdirSync(__dirname+'/node_modules')
let memories = {}

module.exports = function(args){
	for(var module of modules){
		if(module.indexOf('ignore') < 0) {
			let memory = module.replace(/.js/, '')
			memories[memory] = require(`./node_modules/${module}`)
		}
	}
	return memories
}