module.exports = function defaultParser(args){
	if(args.val == null && args.property && args.property.mongoose.schema && args.property.mongoose.schema.default){
		if(typeof args.property.mongoose.schema.default == 'function'){
			return args.property.mongoose.schema.default(args)
		} else {
			return args.property.mongoose.schema.default
		}
	}
	return args.val
}